'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';

interface Student {
  id: string;
  firstName: string;
  lastName: string;
  rollNumber: string;
  class: string;
  section: string;
  photoUrl: string;
  status: string;
  verified: boolean;
  school: {
    schoolName: string;
    address: string;
    city: string;
  };
}

export default function StudentListPage() {
  const router = useRouter();
  const [students, setStudents] = useState<Student[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    fetchStudents();
  }, [statusFilter, searchTerm]);

  const fetchStudents = async () => {
    try {
      const params = new URLSearchParams();
      if (statusFilter !== 'all') params.append('status', statusFilter);
      if (searchTerm) params.append('search', searchTerm);

      const response = await fetch(`/api/students?${params.toString()}`);
      if (response.ok) {
        const result = await response.json();
        setStudents(result.data);
      }
    } catch (error) {
      console.error('Error fetching students:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id: string) => {
    if (!confirm('Are you sure you want to delete this student?')) return;

    try {
      const response = await fetch(`/api/students/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        setStudents(students.filter((s) => s.id !== id));
        alert('Student deleted successfully');
      } else {
        alert('Failed to delete student');
      }
    } catch (error) {
      console.error('Error deleting student:', error);
      alert('Error deleting student');
    }
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'approved':
      case 'completed':
        return 'bg-green-500';
      case 'inactive':
      case 'not printed':
        return 'bg-red-500';
      default:
        return 'bg-gray-500';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-6 flex items-center justify-between">
          <div>
            <div className="text-sm text-gray-500 mb-2">
              Dashboards / Manage Students / List Students
            </div>
            <h1 className="text-2xl font-semibold">LIST STUDENTS</h1>
          </div>
          <Link
            href="/manage-students/add-students"
            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors flex items-center gap-2"
          >
            <span className="text-xl">+</span> Add Student
          </Link>
        </div>

        {/* Status Overview */}
        <div className="bg-white rounded-lg shadow p-4 mb-6">
          <h3 className="font-semibold mb-3">Status Overview</h3>
          <div className="flex gap-4">
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-green-500 rounded-full"></div>
              <span className="text-sm">Approved ({students.filter(s => s.status === 'approved').length})</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-red-500 rounded-full"></div>
              <span className="text-sm">Inactive ({students.filter(s => s.status === 'inactive').length})</span>
            </div>
          </div>
        </div>

        {/* Search and Filter */}
        <div className="bg-white rounded-lg shadow mb-6">
          <div className="p-4 border-b">
            <input
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full md:w-96 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Table */}
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50 border-b">
                <tr>
                  <th className="px-4 py-3 text-left">
                    <input 
                      type="checkbox" 
                      className="rounded"
                      aria-label="Select all students"
                    />
                  </th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">School Type</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">School Code</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">School Name</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">School Location</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Last Updated</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Status</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Action</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {students.map((student) => (
                  <tr key={student.id} className="hover:bg-gray-50">
                    <td className="px-4 py-3">
                      <input 
                        type="checkbox" 
                        className="rounded"
                        aria-label={`Select ${student.firstName} ${student.lastName}`}
                      />
                    </td>
                    <td className="px-4 py-3">
                      <div className="flex items-center gap-3">
                        <div className="w-10 h-10 rounded-full bg-gray-200 overflow-hidden flex-shrink-0">
                          {student.photoUrl ? (
                            <img
                              src={student.photoUrl}
                              alt={`${student.firstName} ${student.lastName}`}
                              className="w-full h-full object-cover"
                            />
                          ) : (
                            <div className="w-full h-full flex items-center justify-center text-gray-400">
                              üë§
                            </div>
                          )}
                        </div>
                        <span className="text-sm">
                          {student.class} - {student.section || 'N/A'}
                        </span>
                      </div>
                    </td>
                    <td className="px-4 py-3 text-sm">{student.rollNumber}</td>
                    <td className="px-4 py-3 text-sm">
                      {student.firstName} {student.lastName}
                    </td>
                    <td className="px-4 py-3 text-sm">
                      {student.school.city}
                    </td>
                    <td className="px-4 py-3 text-sm">
                      {new Date().toLocaleDateString('en-GB', {
                        day: '2-digit',
                        month: '2-digit',
                        year: 'numeric',
                      })}
                    </td>
                    <td className="px-4 py-3">
                      <span
                        className={`px-3 py-1 rounded-full text-xs text-white ${getStatusColor(
                          student.status
                        )}`}
                      >
                        {student.status}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <div className="flex items-center gap-2">
                        <button
                          onClick={() => router.push(`/manage-students/view/${student.id}`)}
                          className="p-2 text-blue-600 hover:bg-blue-50 rounded transition-colors"
                          title="View"
                          aria-label={`View ${student.firstName} ${student.lastName}`}
                        >
                          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                          </svg>
                        </button>
                        <button
                          onClick={() => router.push(`/manage-students/edit/${student.id}`)}
                          className="p-2 text-blue-600 hover:bg-blue-50 rounded transition-colors"
                          title="Edit"
                          aria-label={`Edit ${student.firstName} ${student.lastName}`}
                        >
                          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                          </svg>
                        </button>
                        <button
                          onClick={() => handleDelete(student.id)}
                          className="p-2 text-red-600 hover:bg-red-50 rounded transition-colors"
                          title="Delete"
                          aria-label={`Delete ${student.firstName} ${student.lastName}`}
                        >
                          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                          </svg>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {students.length === 0 && (
            <div className="text-center py-12 text-gray-500">
              No students found. Add your first student to get started.
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="text-center text-sm text-gray-500">
          2025 ¬© Wezant. Design & Developed by ‚ù§Ô∏è Zenoids
        </div>
      </div>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useParams, useRouter } from 'next/navigation';

interface StudentData {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  fatherName: string;
  phone: string;
  parentPhone: string;
  gender: string;
  dob: string;
  rollNumber: string;
  bloodGroup: string;
  photoUrl: string;
  class: string;
  section: string;
  parentGuardianName: string;
  status: string;
  verified: boolean;
  school: {
    schoolName: string;
    address: string;
    city: string;
    state: string;
    pinCode: string;
    phoneNo: string;
    email: string;
    website: string;
  };
}

export default function StudentViewPage() {
  const params = useParams();
  const router = useRouter();
  const [student, setStudent] = useState<StudentData | null>(null);
  const [loading, setLoading] = useState(true);
  const [printStatus, setPrintStatus] = useState('Printed');
  const [verifyStatus, setVerifyStatus] = useState('Verified');

  useEffect(() => {
    const fetchStudent = async () => {
      try {
        const response = await fetch(`/api/students/${params.id}`);
        if (response.ok) {
          const result = await response.json();
          setStudent(result.data);
          setPrintStatus(result.data.status || 'Not Printed');
          setVerifyStatus(result.data.verified ? 'Verified' : 'Not Verified');
        }
      } catch (error) {
        console.error('Error fetching student:', error);
      } finally {
        setLoading(false);
      }
    };

    if (params.id) {
      fetchStudent();
    }
  }, [params.id]);

  const handlePrint = () => {
    window.print();
  };

  const handleSubmit = async () => {
    try {
      const formData = new FormData();
      formData.append('status', printStatus);
      formData.append('verified', verifyStatus === 'Verified' ? 'true' : 'false');

      const response = await fetch(`/api/students/${params.id}`, {
        method: 'PUT',
        body: formData,
      });

      if (response.ok) {
        alert('Status updated successfully!');
        router.push('/manage-students/list');
      } else {
        alert('Failed to update status');
      }
    } catch (error) {
      console.error('Error updating student:', error);
      alert('Error updating student');
    }
  };

  const handleEdit = () => {
    router.push(`/manage-students/edit/${params.id}`);
  };

  const handleSkip = () => {
    router.push('/manage-students/list');
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg">Loading...</div>
      </div>
    );
  }

  if (!student) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg">Student not found</div>
      </div>
    );
  }

  const fullName = `${student.firstName} ${student.lastName}`;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Print Button Bar - Hidden when printing */}
      <div className="no-print fixed bottom-8 left-1/2 transform -translate-x-1/2 bg-white shadow-lg rounded-lg px-6 py-4 flex gap-4 z-50 border border-gray-200">
        <button
          onClick={handlePrint}
          className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors font-medium"
        >
          Print
        </button>
        <button
          onClick={handleSkip}
          className="px-6 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 transition-colors font-medium"
        >
          Skip Print
        </button>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Breadcrumb - Hidden when printing */}
        <div className="no-print mb-4 text-sm text-gray-500">
          Dashboards / Manage Student / Student View
        </div>

        <h1 className="text-2xl font-semibold mb-6">STUDENT VIEW</h1>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Left Section - Student Details */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex gap-6 mb-8">
                {/* Profile Photo */}
                <div className="flex-shrink-0">
                  <div className="w-32 h-32 border-4 border-gray-200 rounded-lg overflow-hidden bg-gray-100">
                    {student.photoUrl ? (
                      <img
                        src={student.photoUrl}
                        alt={fullName}
                        className="w-full h-full object-cover"
                      />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center text-4xl text-gray-400">
                        üë§
                      </div>
                    )}
                  </div>
                  {/* Buttons - Hidden when printing */}
                  <div className="no-print mt-3 space-y-2">
                    <button
                      onClick={() => setPrintStatus('Printed')}
                      className="w-full px-4 py-2 bg-green-500 text-white text-sm rounded-md hover:bg-green-600 transition-colors"
                    >
                      Printed
                    </button>
                    <button
                      onClick={handleEdit}
                      className="w-full px-4 py-2 bg-blue-600 text-white text-sm rounded-md hover:bg-blue-700 transition-colors"
                    >
                      Edit
                    </button>
                  </div>
                </div>

                {/* Basic Info */}
                <div className="flex-1">
                  <div className="flex items-center gap-2 mb-4">
                    <h2 className="text-2xl font-semibold text-gray-800">{fullName}</h2>
                    {student.verified && (
                      <svg className="w-6 h-6 text-blue-500" fill="currentColor" viewBox="0 0 20 20">
                        <path
                          fillRule="evenodd"
                          d="M6.267 3.455a3.066 3.066 0 001.745-.723 3.066 3.066 0 013.976 0 3.066 3.066 0 001.745.723 3.066 3.066 0 012.812 2.812c.051.643.304 1.254.723 1.745a3.066 3.066 0 010 3.976 3.066 3.066 0 00-.723 1.745 3.066 3.066 0 01-2.812 2.812 3.066 3.066 0 00-1.745.723 3.066 3.066 0 01-3.976 0 3.066 3.066 0 00-1.745-.723 3.066 3.066 0 01-2.812-2.812 3.066 3.066 0 00-.723-1.745 3.066 3.066 0 010-3.976 3.066 3.066 0 00.723-1.745 3.066 3.066 0 012.812-2.812zm7.44 5.252a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                          clipRule="evenodd"
                        />
                      </svg>
                    )}
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="text-sm text-gray-500">S/o, D/o :</label>
                      <p className="font-medium text-gray-800">{student.fatherName}</p>
                    </div>

                    <div>
                      <label className="text-sm text-gray-500">DOB :</label>
                      <p className="font-medium text-gray-800">
                        {new Date(student.dob).toLocaleDateString('en-GB', {
                          day: '2-digit',
                          month: '2-digit',
                          year: 'numeric',
                        })}
                      </p>
                    </div>

                    <div>
                      <label className="text-sm text-gray-500">Gender :</label>
                      <p className="font-medium text-gray-800">{student.gender}</p>
                    </div>

                    <div>
                      <label className="text-sm text-gray-500">Blood Group :</label>
                      <p className="font-medium text-gray-800">{student.bloodGroup}</p>
                    </div>

                    <div>
                      <label className="text-sm text-gray-500">Roll No :</label>
                      <p className="font-medium text-gray-800">{student.rollNumber}</p>
                    </div>

                    <div>
                      <label className="text-sm text-gray-500">Class :</label>
                      <p className="font-medium text-gray-800">{student.class}</p>
                    </div>

                    <div>
                      <label className="text-sm text-gray-500">Section :</label>
                      <p className="font-medium text-gray-800">{student.section}</p>
                    </div>
                  </div>
                </div>
              </div>

              {/* School Details Section */}
              <div className="border-t pt-6">
                <h3 className="text-lg font-semibold mb-4">School Details:</h3>
                <div>
                  <p className="font-semibold text-gray-800 mb-2">{student.school.schoolName}</p>
                  <p className="text-sm text-gray-600">
                    # {student.school.address}, {student.school.city}
                  </p>
                  <p className="text-sm text-gray-600">Call: {student.school.phoneNo}</p>
                  <p className="text-sm text-gray-600">Email: {student.school.email}</p>
                  <p className="text-sm text-gray-600">Website: {student.school.website}</p>
                </div>
              </div>

              {/* Parent/Guardian Information */}
              <div className="border-t pt-6 mt-6">
                <h3 className="text-lg font-semibold mb-4">Parents/Guardian Information:</h3>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="text-sm text-gray-500">Name:</label>
                    <p className="font-medium text-gray-800">
                      {student.parentGuardianName || student.fatherName}
                    </p>
                  </div>
                  <div>
                    <label className="text-sm text-gray-500">Phone No. :</label>
                    <p className="font-medium text-gray-800">
                      {student.parentPhone || student.phone}
                    </p>
                  </div>
                </div>
              </div>
            </div>

            {/* Status Section - Hidden when printing */}
            <div className="no-print bg-white rounded-lg shadow p-6 mt-6">
              <div className="flex items-center gap-6">
                <span className="text-gray-700 font-medium">Status</span>
                
                <select
                  value={printStatus}
                  onChange={(e) => setPrintStatus(e.target.value)}
                  aria-label="Print Status"
                  className="px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="Printed">Printed</option>
                  <option value="Not Printed">Not Printed</option>
                </select>

                <select
                  value={verifyStatus}
                  onChange={(e) => setVerifyStatus(e.target.value)}
                  aria-label="Verification Status"
                  className="px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="Verified">Verified</option>
                  <option value="Not Verified">Not Verified</option>
                </select>

                <button
                  onClick={handleSubmit}
                  className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors font-medium"
                >
                  Submit
                </button>
              </div>
            </div>
          </div>

          {/* Right Section - ID Card Preview */}
          <div className="lg:col-span-1">
            <div className="sticky top-6">
              <h3 className="text-lg font-semibold mb-4">Horizontal</h3>
              <div className="bg-gradient-to-br from-purple-600 to-indigo-700 rounded-xl p-6 text-white text-center border-4 border-dashed border-white shadow-xl">
                {/* School Logo */}
                <div className="w-16 h-16 bg-white rounded-full flex items-center justify-center mx-auto mb-3 border-4 border-purple-800">
                  <span className="text-2xl font-bold text-purple-700">S</span>
                </div>

                {/* School Name */}
                <div className="text-red-400 font-bold text-lg mb-4">
                  {student.school.schoolName.toUpperCase()}
                </div>

                {/* Student Photo */}
                <div className="w-24 h-24 bg-green-100 rounded-lg mx-auto mb-3 border-2 border-white overflow-hidden">
                  {student.photoUrl ? (
                    <img
                      src={student.photoUrl}
                      alt={fullName}
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <div className="w-full h-full flex items-center justify-center text-3xl text-gray-500">
                      üë§
                    </div>
                  )}
                </div>

                {/* Student Info */}
                <div className="text-xs mb-1 font-semibold">STUDENT ID: {student.rollNumber}</div>
                <div className="text-sm font-bold mb-1">STUDENT NAME</div>
                <div className="text-xs mb-2">
                  Father&apos;s Name : <span className="font-semibold">{student.fatherName}</span>
                </div>
                <div className="text-xs">
                  Class Name : <span className="font-semibold">{student.class}</span>
                </div>
                <div className="text-xs">
                  Class Roll : <span className="font-semibold">{student.rollNumber}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Print Styles */}
      <style jsx global>{`
        @media print {
          .no-print {
            display: none !important;
          }
          
          body {
            print-color-adjust: exact;
            -webkit-print-color-adjust: exact;
          }
          
          @page {
            margin: 1cm;
          }
        }
      `}</style>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { useTheme } from '@/context/ThemeContext';

interface UserSettings {
  id: string;
  name: string;
  email: string;
  phone: string;
  avatar: string;
  theme: string;
  notifications: boolean;
  role: string;
}

export default function SettingsPage() {
  const router = useRouter();
  const { theme: currentTheme, effectiveTheme, setTheme } = useTheme();
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [activeTab, setActiveTab] = useState<'profile' | 'security' | 'preferences'>('profile');
  
  const [settings, setSettings] = useState<UserSettings>({
    id: '',
    name: '',
    email: '',
    phone: '',
    avatar: '',
    theme: 'light',
    notifications: true,
    role: 'ADMIN',
  });

  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });

  const [avatarFile, setAvatarFile] = useState<File | null>(null);
  const [avatarPreview, setAvatarPreview] = useState<string>('');

  useEffect(() => {
    fetchSettings();
  }, []);

  // Sync theme from context to settings
  useEffect(() => {
    setSettings(prev => ({ ...prev, theme: currentTheme }));
  }, [currentTheme]);

  const fetchSettings = async () => {
    try {
      const response = await fetch('/api/settings');
      if (response.ok) {
        const result = await response.json();
        setSettings(result.data);
        setAvatarPreview(result.data.avatar || '');
        // Set theme from API
        setTheme(result.data.theme || 'light');
      }
    } catch (error) {
      console.error('Error fetching settings:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    
    // Handle theme change immediately
    if (name === 'theme') {
      const newTheme = value as 'light' | 'dark' | 'system';
      setTheme(newTheme); // Apply theme instantly
      setSettings(prev => ({ ...prev, theme: newTheme }));
      // Auto-save theme preference
      saveThemePreference(newTheme);
    } else {
      setSettings(prev => ({
        ...prev,
        [name]: type === 'checkbox' ? checked : value,
      }));
    }
  };

  const saveThemePreference = async (theme: string) => {
    try {
      const formData = new FormData();
      formData.append('name', settings.name);
      formData.append('email', settings.email);
      formData.append('phone', settings.phone);
      formData.append('theme', theme);
      formData.append('notifications', settings.notifications.toString());
      
      if (avatarFile) {
        formData.append('avatar', avatarFile);
      }

      await fetch('/api/settings', {
        method: 'PUT',
        body: formData,
      });
    } catch (error) {
      console.error('Error saving theme:', error);
    }
  };

  const handlePasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleAvatarChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setAvatarFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setAvatarPreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleProfileUpdate = async (e: React.FormEvent) => {
    e.preventDefault();
    setSaving(true);

    try {
      const formData = new FormData();
      formData.append('name', settings.name);
      formData.append('email', settings.email);
      formData.append('phone', settings.phone);
      formData.append('theme', settings.theme);
      formData.append('notifications', settings.notifications.toString());
      
      if (avatarFile) {
        formData.append('avatar', avatarFile);
      }

      const response = await fetch('/api/settings', {
        method: 'PUT',
        body: formData,
      });

      if (response.ok) {
        alert('Profile updated successfully!');
        fetchSettings();
      } else {
        const error = await response.json();
        alert(error.error || 'Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Error updating profile');
    } finally {
      setSaving(false);
    }
  };

  const handlePasswordUpdate = async (e: React.FormEvent) => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('New passwords do not match!');
      return;
    }

    if (passwordData.newPassword.length < 8) {
      alert('Password must be at least 8 characters long');
      return;
    }

    setSaving(true);

    try {
      const response = await fetch('/api/settings/password', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword,
        }),
      });

      if (response.ok) {
        alert('Password updated successfully!');
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: '',
        });
      } else {
        const error = await response.json();
        alert(error.error || 'Failed to update password');
      }
    } catch (error) {
      console.error('Error updating password:', error);
      alert('Error updating password');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg">Loading settings...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors duration-300">
      <div className="max-w-5xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-6">
          <div className="text-sm text-gray-500 dark:text-gray-400 mb-2">Dashboard / Settings</div>
          <h1 className="text-2xl font-semibold text-gray-900 dark:text-gray-100">SETTINGS</h1>
        </div>

        {/* Tabs */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow mb-6 transition-colors duration-300">
          <div className="border-b border-gray-200 dark:border-gray-700">
            <nav className="flex space-x-8 px-6" aria-label="Tabs">
              <button
                onClick={() => setActiveTab('profile')}
                className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                  activeTab === 'profile'
                    ? 'border-blue-500 text-blue-600 dark:text-blue-400'
                    : 'border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300 hover:border-gray-300 dark:hover:border-gray-600'
                }`}
                aria-label="Profile Settings Tab"
              >
                Profile Settings
              </button>
              <button
                onClick={() => setActiveTab('security')}
                className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                  activeTab === 'security'
                    ? 'border-blue-500 text-blue-600 dark:text-blue-400'
                    : 'border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300 hover:border-gray-300 dark:hover:border-gray-600'
                }`}
                aria-label="Security Tab"
              >
                Security
              </button>
              <button
                onClick={() => setActiveTab('preferences')}
                className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                  activeTab === 'preferences'
                    ? 'border-blue-500 text-blue-600 dark:text-blue-400'
                    : 'border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300 hover:border-gray-300 dark:hover:border-gray-600'
                }`}
                aria-label="Preferences Tab"
              >
                Preferences
              </button>
            </nav>
          </div>

          {/* Profile Settings Tab */}
          {activeTab === 'profile' && (
            <form onSubmit={handleProfileUpdate} className="p-6">
              <div className="space-y-6">
                {/* Avatar Upload */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Profile Picture
                  </label>
                  <div className="flex items-center gap-6">
                    <div className="w-24 h-24 rounded-full bg-gray-200 dark:bg-gray-700 overflow-hidden">
                      {avatarPreview ? (
                        <img
                          src={avatarPreview}
                          alt="Profile"
                          className="w-full h-full object-cover"
                        />
                      ) : (
                        <div className="w-full h-full flex items-center justify-center text-4xl text-gray-400 dark:text-gray-500">
                          üë§
                        </div>
                      )}
                    </div>
                    <div>
                      <input
                        type="file"
                        id="avatar"
                        accept="image/*"
                        onChange={handleAvatarChange}
                        className="hidden"
                        aria-label="Upload profile picture"
                      />
                      <label
                        htmlFor="avatar"
                        className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 cursor-pointer inline-block transition-colors"
                      >
                        Change Picture
                      </label>
                      <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                        JPG, PNG or GIF. Max size 5MB
                      </p>
                    </div>
                  </div>
                </div>

                {/* Name */}
                <div>
                  <label htmlFor="name" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Full Name
                  </label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={settings.name}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your full name"
                    className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                  />
                </div>

                {/* Email */}
                <div>
                  <label htmlFor="email" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Email Address
                  </label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={settings.email}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your email"
                    className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                  />
                </div>

                {/* Phone */}
                <div>
                  <label htmlFor="phone" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Phone Number
                  </label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={settings.phone}
                    onChange={handleInputChange}
                    placeholder="Enter your phone number"
                    className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                  />
                </div>

                {/* Role (Read-only) */}
                <div>
                  <label htmlFor="role" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Role
                  </label>
                  <input
                    type="text"
                    id="role"
                    value={settings.role}
                    disabled
                    aria-label="User role (read-only)"
                    className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-gray-100 dark:bg-gray-800 cursor-not-allowed text-gray-500 dark:text-gray-400"
                  />
                </div>

                <div className="flex gap-4">
                  <button
                    type="submit"
                    disabled={saving}
                    className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors disabled:bg-gray-400"
                  >
                    {saving ? 'Saving...' : 'Save Changes'}
                  </button>
                  <button
                    type="button"
                    onClick={fetchSettings}
                    className="px-6 py-2 bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-200 rounded-md hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </form>
          )}

          {/* Security Tab */}
          {activeTab === 'security' && (
            <form onSubmit={handlePasswordUpdate} className="p-6">
              <div className="space-y-6">
                <div>
                  <h3 className="text-lg font-semibold mb-4 text-gray-900 dark:text-gray-100">Change Password</h3>
                  <p className="text-sm text-gray-600 dark:text-gray-400 mb-6">
                    Ensure your account is using a long, random password to stay secure.
                  </p>
                </div>

                {/* Current Password */}
                <div>
                  <label htmlFor="currentPassword" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Current Password
                  </label>
                  <input
                    type="password"
                    id="currentPassword"
                    name="currentPassword"
                    value={passwordData.currentPassword}
                    onChange={handlePasswordChange}
                    required
                    placeholder="Enter current password"
                    className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                  />
                </div>

                {/* New Password */}
                <div>
                  <label htmlFor="newPassword" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    New Password
                  </label>
                  <input
                    type="password"
                    id="newPassword"
                    name="newPassword"
                    value={passwordData.newPassword}
                    onChange={handlePasswordChange}
                    required
                    minLength={8}
                    placeholder="Enter new password"
                    className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                  />
                  <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                    Must be at least 8 characters long
                  </p>
                </div>

                {/* Confirm Password */}
                <div>
                  <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Confirm New Password
                  </label>
                  <input
                    type="password"
                    id="confirmPassword"
                    name="confirmPassword"
                    value={passwordData.confirmPassword}
                    onChange={handlePasswordChange}
                    required
                    placeholder="Confirm new password"
                    className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                  />
                </div>

                <div className="flex gap-4">
                  <button
                    type="submit"
                    disabled={saving}
                    className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors disabled:bg-gray-400"
                  >
                    {saving ? 'Updating...' : 'Update Password'}
                  </button>
                </div>
              </div>
            </form>
          )}

          {/* Preferences Tab */}
          {activeTab === 'preferences' && (
            <form onSubmit={handleProfileUpdate} className="p-6">
              <div className="space-y-6">
                {/* Theme Selection */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-4">
                    Theme Preference
                  </label>
                  <div className="space-y-3">
                    {/* Light Mode */}
                    <label className={`flex items-center gap-3 p-4 border-2 rounded-lg cursor-pointer transition-all ${
                      settings.theme === 'light'
                        ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/20'
                        : 'border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700'
                    }`}>
                      <input
                        type="radio"
                        name="theme"
                        value="light"
                        checked={settings.theme === 'light'}
                        onChange={handleInputChange}
                        className="w-4 h-4 text-blue-600"
                        aria-label="Light mode theme"
                      />
                      <span className="text-2xl">‚òÄÔ∏è</span>
                      <div className="flex-1">
                        <div className="text-sm font-medium text-gray-900 dark:text-gray-100">
                          Light Mode
                        </div>
                        <div className="text-xs text-gray-500 dark:text-gray-400">
                          Use light colors for all pages
                        </div>
                      </div>
                      {settings.theme === 'light' && (
                        <div className="text-blue-600 dark:text-blue-400">‚úì</div>
                      )}
                    </label>

                    {/* Dark Mode */}
                    <label className={`flex items-center gap-3 p-4 border-2 rounded-lg cursor-pointer transition-all ${
                      settings.theme === 'dark'
                        ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/20'
                        : 'border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700'
                    }`}>
                      <input
                        type="radio"
                        name="theme"
                        value="dark"
                        checked={settings.theme === 'dark'}
                        onChange={handleInputChange}
                        className="w-4 h-4 text-blue-600"
                        aria-label="Dark mode theme"
                      />
                      <span className="text-2xl">üåô</span>
                      <div className="flex-1">
                        <div className="text-sm font-medium text-gray-900 dark:text-gray-100">
                          Dark Mode
                        </div>
                        <div className="text-xs text-gray-500 dark:text-gray-400">
                          Use dark colors for all pages
                        </div>
                      </div>
                      {settings.theme === 'dark' && (
                        <div className="text-blue-600 dark:text-blue-400">‚úì</div>
                      )}
                    </label>

                    {/* System Mode */}
                    <label className={`flex items-center gap-3 p-4 border-2 rounded-lg cursor-pointer transition-all ${
                      settings.theme === 'system'
                        ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/20'
                        : 'border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700'
                    }`}>
                      <input
                        type="radio"
                        name="theme"
                        value="system"
                        checked={settings.theme === 'system'}
                        onChange={handleInputChange}
                        className="w-4 h-4 text-blue-600"
                        aria-label="System default theme"
                      />
                      <span className="text-2xl">üíª</span>
                      <div className="flex-1">
                        <div className="text-sm font-medium text-gray-900 dark:text-gray-100">
                          System Default
                        </div>
                        <div className="text-xs text-gray-500 dark:text-gray-400">
                          Automatically switch based on system settings
                        </div>
                      </div>
                      {settings.theme === 'system' && (
                        <div className="text-blue-600 dark:text-blue-400">‚úì</div>
                      )}
                    </label>
                  </div>
                </div>

                {/* Current Theme Indicator */}
                <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-700 rounded-lg p-4">
                  <div className="flex items-center gap-3">
                    <span className="text-2xl">
                      {effectiveTheme === 'light' ? '‚òÄÔ∏è' : 'üåô'}
                    </span>
                    <div>
                      <p className="text-sm font-medium text-blue-900 dark:text-blue-100">
                        Currently using: {effectiveTheme} mode
                      </p>
                      <p className="text-xs text-blue-700 dark:text-blue-300">
                        Theme changes apply immediately and are saved automatically
                      </p>
                    </div>
                  </div>
                </div>

                {/* Notifications */}
                <div className="pt-6 border-t border-gray-200 dark:border-gray-700">
                  <label className="flex items-center cursor-pointer">
                    <input
                      type="checkbox"
                      name="notifications"
                      checked={settings.notifications}
                      onChange={handleInputChange}
                      className="mr-3 rounded w-4 h-4 text-blue-600"
                      aria-label="Enable notifications"
                    />
                    <div>
                      <span className="text-sm font-medium text-gray-700 dark:text-gray-200">
                        Enable Notifications
                      </span>
                      <p className="text-xs text-gray-500 dark:text-gray-400">
                        Receive notifications about updates and activities
                      </p>
                    </div>
                  </label>
                </div>

                <div className="flex gap-4">
                  <button
                    type="submit"
                    disabled={saving}
                    className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors disabled:bg-gray-400"
                  >
                    {saving ? 'Saving...' : 'Save Preferences'}
                  </button>
                </div>
              </div>
            </form>
          )}
        </div>

        {/* Footer */}
        <div className="text-center text-sm text-gray-500 dark:text-gray-400">
          2020 ¬© Wezant. Design & Develop by ‚ù§Ô∏è Zenoids
        </div>
      </div>
    </div>
  );
}

'use client'

import { signIn } from "next-auth/react"
import { Label } from "@/components/ui/label"
import Link from "next/link"
import { Checkbox } from "@/components/ui/checkbox"
import { Button } from "@/components/ui/button"
import { useState } from "react"
import { useRouter } from "next/navigation"
import { Loader2 } from "lucide-react"
import { Input } from "@/components/ui/input"

export default function SignInPage() {
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [error, setError] = useState("")
  const [formData, setFormData] = useState({
    identifier: "",
    password: "",
    remember: false
  })
  const router = useRouter()

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }))
    // Clear error when user starts typing
    if (error) setError("")
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setIsSubmitting(true)
    setError("")

    try {
      const result = await signIn('credentials', {
        identifier: formData.identifier,
        password: formData.password,
        redirect: false,
      })

      if (result?.error) {
        setError(result.error)
      } else if (result?.ok) {
        // Successful login - redirect to dashboard
        router.push('/dashboard')
        router.refresh() // Refresh to update session
      }
    } catch (error) {
      console.error('Login error:', error)
      setError("An unexpected error occurred")
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <div className="min-h-screen flex">
      {/* Left side - Full image */}
      <div className="hidden lg:flex w-1/2 bg-gray-100">
        <img
          src="/login_download.png"
          alt="Sign in illustration"
          className="object-cover w-full h-full"
        />
      </div>

      {/* Right side - Sign In Form */}
      <div className="flex w-full lg:w-1/2 items-center justify-center p-8 bg-white">
        <div className="w-full max-w-md space-y-6">
          {/* Logo */}
          <div>
            <h1 className="text-2xl font-bold text-blue-600">True Feedback</h1>
          </div>

          {/* Welcome message */}
          <div>
            <h2 className="text-lg font-semibold text-blue-600">
              Welcome Back !
            </h2>
            <p className="text-sm text-gray-500">
              Sign in to continue to True Feedback.
            </p>
          </div>

          {/* Error message */}
          {error && (
            <div className="p-3 text-sm text-red-600 bg-red-50 border border-red-200 rounded-md">
              {error}
            </div>
          )}

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Username/Email */}
            <div className="space-y-2">
              <Label htmlFor="identifier">Username or Email</Label>
              <Input
                id="identifier"
                name="identifier"
                placeholder="Enter username or email"
                value={formData.identifier}
                onChange={handleInputChange}
                required
              />
            </div>

            {/* Password */}
            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                name="password"
                type="password"
                placeholder="Enter password"
                value={formData.password}
                onChange={handleInputChange}
                required
              />
            </div>

            {/* Remember me */}
            <div className="flex items-center space-x-2">
              <Checkbox
                id="remember"
                name="remember"
                checked={formData.remember}
                onCheckedChange={(checked) =>
                  setFormData(prev => ({ ...prev, remember: checked as boolean }))
                }
              />
              <Label htmlFor="remember" className="text-sm">
                Remember me
              </Label>
            </div>

            {/* Submit */}
            <Button
              type="submit"
              className="w-full bg-blue-700 hover:bg-blue-800 text-white"
              disabled={isSubmitting}
            >
              {isSubmitting ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Logging in...
                </>
              ) : (
                "Log In"
              )}
            </Button>
          </form>

          {/* Forgot password */}
          <div className="text-center">
            <Link href="/forgot-password" className="text-sm text-blue-600 hover:underline">
              Forgot your password?
            </Link>
          </div>

          {/* Signup link */}
          <p className="text-center text-sm text-gray-600">
            Don't have an account?{" "}
            <Link href="/sign-up" className="text-blue-600 hover:underline">
              Sign up now
            </Link>
          </p>

          {/* Footer */}
          <div className="text-center text-xs text-gray-500 mt-8">
            ¬© 2025 True Feedback. All rights reserved.
          </div>
        </div>
      </div>
    </div>
  )
}

'use client';

import { Input } from '@/components/ui/input';
import { useParams, useRouter } from 'next/navigation';
import { zodResolver } from '@hookform/resolvers/zod';
import { Button } from '@/components/ui/button';
import { ApiResponse } from '@/types/ApiResponse';
import axios, { AxiosError } from 'axios';
import { verifySchema } from '@/schemas/verifySchema';
import {
  Form,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  FormControl,
} from '@/components/ui/form';
import { useToast } from '@/hooks/use-toast';
import { useForm } from 'react-hook-form';
import { useState } from 'react';
import { Loader2 } from 'lucide-react';
import * as z from 'zod';

export default function VerifyAccount() {
  const router = useRouter();
  const params = useParams<{ username: string }>();
  const { toast } = useToast();
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  const form = useForm<z.infer<typeof verifySchema>>({
    resolver: zodResolver(verifySchema),
    defaultValues: {
      code: '',
    },
  });

  const onSubmit = async (data: z.infer<typeof verifySchema>) => {
    console.log('üîÑ Submitting verification code:', data.code);
    console.log('üë§ Username from params:', params.username);
    
    setIsSubmitting(true);
    
    try {
      const response = await axios.post<ApiResponse>(`/api/verify-code`, {
        username: params.username,
        code: data.code,
      });

      console.log('‚úÖ Verification successful:', response.data);

      toast({
        title: 'Success',
        description: response.data.message,
      });

      router.replace('/sign-in');
    } catch (error) {
      console.error('‚ùå Verification failed:', error);
      
      const axiosError = error as AxiosError<ApiResponse>;
      toast({
        title: 'Verification Failed',
        description:
          axiosError.response?.data.message ??
          'An error occurred. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md p-8 space-y-8 bg-white rounded-lg shadow-md">
        <div className="text-center">
          <h1 className="text-4xl font-extrabold tracking-tight lg:text-5xl mb-6">
            Verify Your Account
          </h1>
          <p className="mb-4">Enter the verification code sent to your email</p>
          <p className="text-sm text-gray-600">Username: {params.username}</p>
        </div>
        
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <FormField
              name="code"
              control={form.control}
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Verification Code</FormLabel>
                  <FormControl>
                    <Input 
                      {...field} 
                      placeholder="Enter 6-digit code"
                      maxLength={6}
                      className="text-center text-lg tracking-widest"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            
            <Button 
              type="submit" 
              className="w-full" 
              disabled={isSubmitting}
            >
              {isSubmitting ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Verifying...
                </>
              ) : (
                'Verify Account'
              )}
            </Button>
          </form>
        </Form>
        
        <div className="text-center">
          <p className="text-sm text-gray-600">
            Didn't receive the code?{' '}
            <button 
              onClick={() => {
                // You can add resend functionality here later
                toast({
                  title: 'Info',
                  description: 'Please check your spam folder or try signing up again.',
                });
              }}
              className="text-blue-600 hover:underline"
            >
              Resend Code
            </button>
          </p>
        </div>
      </div>
    </div>
  );
}

// src/components/IdCardPreviewModal.tsx
'use client';

import React from 'react';
import Image from 'next/image';

interface IdCardPreviewModalProps {
  isOpen: boolean;
  onClose: () => void;
  schoolData: {
    schoolName: string;
    logoUrl?: string;
    idCardDesignUrl?: string;
  };
}

export default function IdCardPreviewModal({ isOpen, onClose, schoolData }: IdCardPreviewModalProps) {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b">
          <h2 className="text-xl font-semibold text-gray-800">Add School - Card Design Popup</h2>
          <button
            onClick={onClose}
            className="w-8 h-8 flex items-center justify-center rounded-full bg-black text-white hover:bg-gray-800"
            aria-label="Close modal"
          >
            √ó
          </button>
        </div>

        {/* Content */}
        <div className="p-6">
          {/* ID Card Preview */}
          <div className="flex justify-center mb-6">
            {schoolData.idCardDesignUrl ? (
              <div className="relative w-80 h-[500px]">
                <Image
                  src={schoolData.idCardDesignUrl}
                  alt="ID Card Design"
                  fill
                  className="object-contain"
                />
              </div>
            ) : (
              <div className="w-80 h-[500px] bg-gradient-to-b from-purple-300 via-blue-300 to-blue-400 rounded-lg shadow-lg p-6 flex flex-col items-center">
                {/* School Logo */}
                <div className="w-20 h-20 rounded-full bg-white flex items-center justify-center mb-4">
                  {schoolData.logoUrl ? (
                    <Image
                      src={schoolData.logoUrl}
                      alt="School Logo"
                      width={60}
                      height={60}
                      className="rounded-full object-cover"
                    />
                  ) : (
                    <div className="w-16 h-16 rounded-full bg-gray-200"></div>
                  )}
                </div>

                {/* School Name */}
                <div className="text-red-600 font-bold text-lg mb-6 text-center">
                  {schoolData.schoolName || 'SCHOOL NAME'}
                </div>

                {/* Student Photo Placeholder */}
                <div className="w-32 h-40 bg-white rounded-lg border-4 border-teal-400 mb-4 flex items-center justify-center">
                  <div className="w-28 h-36 bg-gray-200 rounded"></div>
                </div>

                {/* Student Info */}
                <div className="text-center space-y-1 text-sm">
                  <div className="text-blue-600 font-semibold">STUDENT ID: MPS-M-001</div>
                  <div className="font-bold text-gray-800">STUDENT NAME</div>
                  <div className="text-xs text-gray-700">Father's Name: ARMAN AMAN</div>
                  <div className="text-xs text-gray-700">Mother's Name: AMNA</div>
                  <div className="text-xs text-gray-700">Class Roll: DY25</div>
                </div>
              </div>
            )}
          </div>

          {/* Message */}
          <div className="text-center text-gray-600 mb-6">
            <p>Your school ID card design has been saved successfully!</p>
          </div>

          {/* Buttons */}
          <div className="flex justify-end gap-4">
            <button
              onClick={onClose}
              className="px-6 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
            >
              Close
            </button>
            <button
              onClick={() => {
                onClose();
                // Navigate to view page or list
                window.location.href = '/manage-school/list-school';
              }}
              className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              View Schools
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
